#!/bin/bash
# update-keystore.sh
# 実際のKeystore内容に更新するスクリプト

# 出力先ディレクトリの存在確認
mkdir -p android/app

# 共有されたbase64データをキーストアにデコード
echo "" | base64 --decode > android/app/stilya-keystore.jks

# Keystoreのパーミッション設定
chmod 600 android/app/stilya-keystore.jks

# credentials.jsonの再作成
cat > credentials.json << EOL
{
  "android": {
    "keystore": {
      "keystorePath": "android/app/stilya-keystore.jks",
      "keystorePassword": "jpn3025Koki",
      "keyAlias": "stilya-key-alias",
      "keyPassword": "jpn3025Koki"
    }
  }
}
EOL

# GitHub用のメッセージ表示
echo "✅ 実際のKeystoreファイルが正常に設定されました"
echo "📝 credentials.jsonも更新されました"
echo "🔒 GitHub Secretsに以下の4つの値を設定してください："
echo "   - ANDROID_KEYSTORE_BASE64: base64エンコードされたKeystoreファイル（すでに共有されたもの）"
echo "   - ANDROID_KEY_ALIAS: stilya-key-alias"
echo "   - ANDROID_KEYSTORE_PASSWORD: jpn3025Koki"
echo "   - ANDROID_KEY_PASSWORD: jpn3025Koki"
